get_surburb_summary.reduce:
function (keys, values, rereduce) {
  var sum1 = 0;
  var sum2 = 0;
  for (i in values){
    sum1 += values[i][0];
    sum2 += values[i][1];
  }
  return [sum1, sum2];
}

get_surburb_summary.map:
function (doc) {
  if (doc.data.geo.surburb > 0){
    if (doc.data.words_of_interest.length > 0){
      emit(doc.data.geo.surburb, [1,1]);
    }
    else{
      emit(doc.data.geo.surburb, [0,1]);
    }
  }
}

