melbourne_surburb_time_interval.map:
function (doc) {
  var surburb = doc.data.geo.surburb;
  var date = doc.data.created_at_melb_time;
  var year = date[0];
  var hour = date[3];
  var minInterval = Math.floor(date[4]/15)
  if (doc.data.words_of_interest.length>0){
    emit([surburb, year, hour, minInterval], [1,1])
  }
  else{
    emit([surburb, year, hour, minInterval], [0,1])
  }
}

