get_surburb_summary_image.reduce:
function (keys, values, rereduce) {
  var sum1 = 0;
  var sum2 = 0;
  var sum3 = 0;
  for (i in values){
    sum1 += values[i][0];
    sum2 += values[i][1];
    sum3 += values[i][2];
  }
  return [sum1, sum2, sum3];
}

get_surburb_summary_image.map:
function (doc) {
  if (doc.data.geo.surburb > 0){
    if (doc.data.words_of_interest.length > 0){
      emit(doc.data.geo.surburb, [1,0,0]);
    }
    if(doc.data.images.length > 0){
      emit(doc.data.geo.surburb, [0,0,1]);
    }
    emit(doc.data.geo.surburb, [0,1,0])
  }
}

